(def n 100000)

(defn std-normal-polar
  []
  (->> (repeatedly #(vector (rand) (rand)))
       (map (fn [[u1 u2]]
              (let [r (Math/sqrt (* -2 (Math/log u1)))
                    theta (* 2 Math/PI u2)]
                [r theta])))
       (mapcat (fn [[r theta]]
                 [(* r (Math/cos theta))
                  (* r (Math/sin theta))]))))

(def sample (take n (std-normal-polar)))

(plot/compose
  (plot/histogram sample
                  :bins 100
                  :normalize :probability-density)
  (plot/plot std-norm [-10 10]
             :plot-points 200))

