(def n 1000000)

(defn std-normal-polar-fast
  [n]
  {:pre [(== (mod n 2) 0)]}
  (let [sample (make-array Double/TYPE n)]
    (loop [i 0]
      (let [u1 (rand)
            u2 (rand)
            r (Math/sqrt (* -2 (Math/log u1)))
            theta (* 2 Math/PI u2)]
        (aset ^doubles sample i
              ^double (* r (Math/cos theta)))
        (aset ^doubles sample (inc i)
              ^double (* r (Math/sin theta))))
      (when (< (inc i) (dec n))
        (recur (long (+ i 2)))))
    sample))

(plot/compose
  (plot/histogram (std-normal-polar-fast n)
                  :bins 100
                  :normalize :probability-density)
  (plot/plot std-norm [-10 10]
             :plot-points 200))
