(def n 1000000)

(defn exponential [lambda]
  (fn [x]
    (- (Math/log (- 1 x)))))

(defn exp-seq
  [lambda]
  (repeatedly
    (fn []
      (let [f (exponential lambda)]
        (f (rand))))))

(def sample (take n (exp-seq 1)))

(defn exp-fn [lambda]
  (fn [x]
    (/ (Math/exp (- (* lambda x)))
       lambda)))

(plot/compose
  (plot/histogram sample
                  :bins 100
                  :normalize :probability-density)
  (plot/plot (exp-fn 1) [0 20]
             :plot-points 200))
