(def n 1000000)

(defn std-normal-polar
  []
  (->> (repeatedly (fn []
                     (let [u1 (rand)
                           u2 (rand)
                           r (Math/sqrt (* -2 (Math/log u1)))
                           theta (* 2 Math/PI u2)]
                       [(* r (Math/cos theta))
                        (* r (Math/sin theta))])))
       flatten))

(def sample (take n (std-normal-polar)))

(plot/compose
  (plot/histogram sample
                  :bins 100
                  :normalize :probability-density)
  (plot/plot std-norm [-10 10]
             :plot-points 200))

